import React from "react";

class NewPost extends React.Component {
  constructor() {
    super();
    this.state = {
      title: undefined,
      description: undefined,
      body: undefined,
      tags: undefined,
      errors: {
        title: undefined,
        description: undefined,
        tags: undefined,
        body: undefined,
      },
    };
  }
  handleInput = ({ target }) => {
    let { name, value } = target;
    this.setState({
      [name]: value,
    });
  };
  render() {
    let { title, description, tags, body } = this.state;
    return (
      <div className="bg-creme">
        <h2 className="capitalize text-2xl text-center p-4 blue ">
          add article
        </h2>
        <div className="text-center m-8 w-3/5">
          <form>
            <input
              placeholder="Enter Article Title"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="text"
              name="title"
              value={title}
            ></input>
          </form>
        </div>
      </div>
    );
  }
}

export default NewPost;
