import baseUrl from "./constants";

export default function fetchData(filter, activeIndex, fn, tag) {
  let Tag = tag ? tag : "";
  let Filter = filter ? filter : "";

  fetch(`/api/articles?${Tag}limit=10&offset=${activeIndex * 10}${Filter}`)
    .then((res) => {
      if (!res.ok) {
        throw new Error(res.statusText);
      }
      return res.json();
    })
    .then((data) => {
      if (data.articles) {
        fn("articles", data.articles);
        fn("articlesCount", data.articlesCount);
      } else {
        fn("error", data);
      }
    })
    .catch((err) => {
      fn("error", "not able to fetch data");
    });
}
