import React from "react";
import Hero from "./hero";

class Article extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      article: null,
    };
  }
  componentDidMount() {
    const slug = this.props.match.params.slug;
    fetch(`https://mighty-oasis-08080.herokuapp.com/api/articles/${slug}`)
      .then((res) => res.json())
      .then((article) => {
        this.setState({
          article: article,
        });
      });
  }
  render() {
    let article;
    if (this.state.article) {
      article = this.state.article.article;
    }
    return (
      <div>
        {this.state.article ? (
          <>
            <Hero title={article.title} description={article.author.username} />
            <div className="mx-8 my-4">
              <img
                className="h-8 w-8 my-3 rounded-full"
                src={article.author.image}
                alt={article.author.username}
              ></img>
              <p className="text-lg py-4">{article.description}</p>
              <p className="text-lg py-4">{article.slug}</p>
              <p className="text-lg py-4">{article.tagList}</p>
            </div>
          </>
        ) : (
          ""
        )}
      </div>
    );
  }
}
export default Article;
