export default function fetchData(filter, activeIndex, fn) {
  let authorFilter;
  authorFilter = filter;

  fetch(
    `https://mighty-oasis-08080.herokuapp.com/api/articles?limit=10&offset=${
      activeIndex * 10
    }${authorFilter}`
  )
    .then((res) => {
      if (!res.ok) {
        throw new Error(res.statusText);
      }
      return res.json();
    })
    .then((data) => {
      fn("articles", data.articles);
      fn("articlesCount", data.articlesCount);
    })
    .catch((err) => {
      this.setState({
        error: "not able to fetch articles",
      });
    });
}
