import React from "react";
import { NavLink } from "react-router-dom";

export default class Comment extends React.Component {
  constructor(props) {
    super();
    this.state = {
      comment: "",
      error: "",
    };
  }
  handleInput = ({ target }) => {
    let { name, value } = target;
    this.setState({
      [name]: value,
    });
  };
  checkInput = () => {
    let { comment } = this.state;
    if (!comment) {
      this.setState({
        error: "comment required to add*",
      });
    }
  };
  addComment = () => {
    let { comment } = this.state;
    fetch(
      `https://mighty-oasis-08080.herokuapp.com/api/articles/${this.props.slug}`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          authorization: `Token ${this.props.user.token}`,
        },
        body: JSON.stringify({
          comment: {
            body: comment,
          },
        }),
      }
    )
      .then((res) => {
        if (!res.ok) {
          throw new Error("cant add new comment");
        }
        return res.json();
      })
      .then((comment) => {
        console.log(comment);
      })
      .catch((errors) => {
        this.setState({
          error: errors.error,
        });
      });
  };
  render() {
    let { comment, error } = this.state;
    let formControlClass =
      "text-sm rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900";

    return (
      <div className=" w-1/3 mx-auto">
        {this.props.user ? (
          <form
            onSubmit={(event) => {
              event.preventDefault();
              this.checkInput();
              if (!error) {
                this.addComment();
              }
            }}
          >
            <span className="text-red-500 text-sm">{this.state.comment}</span>
            <textarea
              className={formControlClass}
              placeholder="Write comment"
              rows={3}
              onChange={this.handleInput}
              value={comment}
              name="comment"
            ></textarea>
            <input
              type="submit"
              value="Add Comment"
              className={`bg-blue-300 blue ${formControlClass}`}
            ></input>
          </form>
        ) : (
          <NavLink to={"/login"}>
            <p className="text-base m-4 text-center">
              <span className="text-green-500">SignIn/SignUp</span> to add
              <span className="text-green-500"> Comment</span>
            </p>
          </NavLink>
        )}
      </div>
    );
  }
}
