import React from "react";

class NewPost extends React.Component {
  constructor() {
    super();
    this.state = {
      title: undefined,
      description: undefined,
      body: undefined,
      tags: undefined,
      error: undefined,
    };
  }
  handleInput = ({ target }) => {
    let { name, value } = target;
    this.setState({
      [name]: value,
    });
  };
  checkInput = () => {
    let { title, description, tags, body } = this.state;
    if (!title || !description || !tags || !body) {
      this.setState({
        error: "all fields are required*",
      });
    }
  };
  render() {
    let { title, description, tags, body } = this.state;
    return (
      <div className="bg-creme">
        <h2 className="capitalize text-2xl text-center p-4 blue ">
          add article
        </h2>
        <div className="text-center mx-auto w-3/5">
          <form
            onSubmit={(event) => {
              event.preventDefault();
              this.checkInput();
            }}
          >
            <span className="text-red">
              {this.state.error ? this.state.error : ""}
            </span>
            <input
              placeholder="Enter Article Title"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="text"
              name="title"
              value={title}
            ></input>
            <input
              placeholder="What's this article is about?"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="text"
              name="description"
              value={description}
            ></input>
            <textarea
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              placeholder="Write your article"
              rows={6}
              onChange={this.handleInput}
              value={body}
              name="body"
            ></textarea>
            <input
              placeholder="Enter tags"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="text"
              name="tags"
              value={tags}
            ></input>
            <input
              type="submit"
              value="Add Article"
              className="text-lg rounded-md w-full py-1 px-4 bg-blue-300 blue my-2 border-2 border-solid border-blue-900 text-blue-900"
            ></input>
          </form>
        </div>
      </div>
    );
  }
}

export default NewPost;
