  constructor(props) {
    super(props);
    this.state = {
      article: null,
      error: null,
      profile: null,
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = () => {
    const slug = this.props.match.params.slug;
    fetch(`/api/articles/${slug}`)
      .then((res) => {
        if (!res.ok) {
          throw new Error(res.statusText);
        }
        return res.json();
      })
      .then((article) => {
        this.setState(
          {
            article: article.article,
