          // }
        );
      })
      .catch((err) => {
        this.setState({
          error: "not able to fetch article",
        });
      });
  };
  deleteArticle = () => {
    fetch(`${baseUrl}articles/${this.state.article.article.slug}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        authorization: `Token ${this.props.user.token}`,
      },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("can't delete article");
        }
        this.props.history.push(`/`);
      })
      .catch((errors) => {
        this.setState({
          error: errors.error,
        });
      });
  };
  followAuthor = () => {
    fetch(`${baseUrl}profiles/${this.state.profile.username}/follow`, {
      method: this.state.profile.following ? "DELETE" : "POST",
      headers: {
        "Content-Type": "application/json",
        authorization: `Token ${this.props.user.token}`,
      },
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("can't follow author");
        }
        return res.json();
      })
      .then((profile) => {
        this.setState({
          profile: profile.profile,
        });
      })
      .catch((errors) => {
        this.setState({
          error: errors.error,
        });
      });
  };
  favoriteArticle = (slug) => {
    if (this.props.user) {
      fetch(`${baseUrl}articles/${slug}/favorite`, {
        method: this.state.article.article.favorited ? "DELETE" : "POST",
        headers: {
          "Content-Type": "application/json",
          authorization: `Token ${this.props.user.token}`,
        },
      })
        .then((res) => {
          if (!res.ok) {
            throw new Error("can't mark as favorite article");
