        .then((article) => {
          this.setState({
            article: article,
          });
        })
        .catch((errors) => {
          this.setState({
            error: errors.error,
          });
        });
    } else {
      alert("you have to log in first");
    }
  };
  render() {
    let article, author;
    if (this.state.article) {
      article = this.state.article.article;
      console.log(article);
      // author = (
