import React from "react";
import { BrowserRouter, NavLink } from "react-router-dom";

class Header extends React.Component {
  render() {
    return (
      <div className="py-2 px-4 flex justify-between items-center bg-blue">
        <h2 className="text-xl pink">Blog App</h2>
        <BrowserRouter>
          <nav className="flex">
            <NavLink to={"/"} className="capitalize pink text-base mx-4">
              Home
            </NavLink>
            <NavLink to={"/login"} className="capitalize pink text-base mx-4">
              login
            </NavLink>
            <NavLink to={"/signup"} className="capitalize pink text-base mx-4">
              signup
            </NavLink>
          </nav>
        </BrowserRouter>
      </div>
    );
  }
}

export default Header;
