function validateEmail(email) {
  const re =
    /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
  return re.test(email);
}
export default function validate(errors, name, value) {
  switch (name) {
    case "username":
      errors.username =
        value.length < 6 ? "length can't be less than 6 characters" : "";
      break;
    case "email":
      errors.email = validateEmail(value) ? "" : "email not valid";
      break;

    default:
      return errors;
  }
}
