import React from "react";
import Hero from "./hero";
import Sidebar from "./sidebar";
import ArticlesFeed from "./articlesFeed";
import { BrowserRouter } from "react-router-dom";
import Feed from "./feed";

class Home extends React.Component {
  constructor() {
    super();
    this.state = {
      openTag: "",
      articles: null,
      activeIndex: 0,
      articlesCount: 0,
      error: "",
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = (offset = 0) => {
    const tag = this.state.openTag ? `tag=${this.state.openTag}&` : "";
    fetch(
      `https://mighty-oasis-08080.herokuapp.com/api/articles?${tag}limit=10&offset=${offset}`
    )
      .then((res) => {
        if (!res.ok) {
          throw new Error(res.statusText);
        }
        return res.json();
      })
      .then((data) => {
        this.setState({
          articles: data.articles,
          articlesCount: data.articlesCount,
        });
      })
      .catch((err) => {
        this.setState({
          error: "not able to fetch articles",
        });
      });
  };
  handleState = (key, value) => {
    this.setState(
      {
        [key]: value,
      },
      this.fetchData
    );
  };
  render() {
    let num = [],
      count;

    count = Math.floor(this.state.articlesCount / 10);
    for (let i = 0; i < count; i++) {
      num.push(i + 1);
    }
    return (
      <div className="main">
        <Hero />
        <div className="flex justify-end">
          <div className="home w-2/4">
            <Feed handleState={this.handleState} openTag={this.state.openTag} />
            <ArticlesFeed
              articles={this.state.articles}
              openTag={this.state.openTag}
            />
            {num && num.length > 1 ? (
              <>
                <h4 className="p-1 text-center text-xl text-green-600 capitalize">
                  pages
                </h4>
                <div className="flex justify-center w-full flex-wrap p-4">
                  {num.map((n, i) => {
                    return (
                      <button
                        onClick={() => {
                          this.setState({
                            activeIndex: i,
                          });
                          this.props.fetchData(i * 10);
                        }}
                        className={`bg-none text-sm py-1 px-2 rounded-md border-solid border-2 border-green-600 m-1 ${
                          this.state.activeIndex === i
                            ? "bg-green-600 text-white"
                            : ""
                        }`}
                      >
                        {i + 1}
                      </button>
                    );
                  })}
                </div>
              </>
            ) : (
              ""
            )}
          </div>
          <Sidebar handleState={this.handleState} tags={this.state.tags} />
        </div>
      </div>
    );
  }
}

export default Home;
