import React from "react";

class Settings extends React.Component {
  constructor() {
    super();
    this.state = {
      username: undefined,
      imageURL: undefined,
      email: undefined,
      bio: undefined,
      password: undefined,
    };
  }
  render() {
    let { imageURL, username, bio, password, email } = this.state;
    return (
      <div className="bg-creme pb-28">
        <h2 className="capitalize text-2xl text-center p-4 blue ">
          add article
        </h2>
        <div className="text-center mx-auto w-3/5">
          <form
            onSubmit={(event) => {
              event.preventDefault();
            }}
          >
            <input
              placeholder="Enter profile picture URL"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="text"
              name="imageURL"
              value={imageURL}
            ></input>
            <input
              placeholder="Enter username"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="text"
              name="username"
              value={username}
            ></input>
            <input
              placeholder="Enter email"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="email"
              name="email"
              value={email}
            ></input>
            <textarea
              placeholder="Enter bio"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              name="bio"
              value={bio}
              rows={3}
            ></textarea>
            <input
              placeholder="Enter password"
              onChange={this.handleInput}
              className="text-lg rounded-md w-full py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
              type="password"
              name="password"
              value={password}
            ></input>
            <input
              type="submit"
              value="Update Details"
              className="text-lg rounded-md w-full py-1 px-4 bg-blue-300 blue my-2 border-2 border-solid border-blue-900 text-blue-900"
            ></input>
          </form>
        </div>
      </div>
    );
  }
}

export default Settings;
