import React from "react";

class NewPost extends React.Component {
  state = {
    title: null,
    description: null,
    body: null,
    tags: null,
    errors: {
      title: null,
      description: null,
      tags: null,
      body: null,
    },
  };
  handleInput = ({ target }) => {
    let { name, value } = target;
    this.setState({
      [name]: value,
    });
  };
  render() {
    let { title, description, tags, body } = this.state;
    return (
      <div className="bg-creme">
        <h2 className="capitalize text-2xl text-center p-4 blue ">
          add article
        </h2>
        <form>
          <input
            placeholder="Enter Article Title"
            onChange={this.handleInput}
            className="text-lg rounded-md w-70 py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
            type="text"
            name="title"
            value={title}
          ></input>
        </form>
      </div>
    );
  }
}

export default NewPost;
