import React from "react";
import { BrowserRouter, NavLink } from "react-router-dom";

class Articles extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: null,
    };
  }
  componentDidMount(limit = 10, offset = 0) {
    console.log(
      `https://mighty-oasis-08080.herokuapp.com/api/articles?limit=${limit}&offset=${offset}`
    );
    fetch(
      `https://mighty-oasis-08080.herokuapp.com/api/articles?limit=${limit}&offset=${offset}`
    )
      .then((res) => res.json())
      .then((data) => {
        this.setState({
          articles: data,
        });
      })
      .catch((err) => console.log(err));
  }
  render() {
    let num = [],
      count,
      articles;
    if (this.state.articles) {
      count = this.state.articles.articlesCount % 10;
      for (let i = 0; i < count; i++) {
        num.push(i + 1);
      }
      articles = this.state.articles.articles.filter((article) => {
        if (this.props.openTag === false) {
          return article;
        } else if (article.tagList.includes(this.props.openTag)) {
          return article;
        }
      });
    }

    return (
      <section className="articles">
        {this.state.articles
          ? articles.map((article, i) => {
              return <Article article={article} key={i} />;
            })
          : ""}
        {articles && articles.length > 10 ? (
          <div className="flex justify-center">
            {num.map((n, i) => {
              return (
                <button
                  onClick={() => {
                    const limit = 10;
                    const offset = i * 10;
                    this.componentDidMount(limit, offset);
                  }}
                  className="bg-none text-sm py-1 px-2 rounded-md border-solid border-2 border-green-600 m-1"
                >
                  {i + 1}
                </button>
              );
            })}
          </div>
        ) : (
          ""
        )}
      </section>
    );
  }
}

function Article(props) {
  const article = props.article;
  return (
    <div className="article cursor-pointer justify-between flex p-8 m-4 border-2 border-solid rounded-lg border-blue-900">
      <div className="w-4/5">
        <h3 className="text-2xl pink">{article.title}</h3>
        <div className="flex py-2 items-center">
          <img
            className="h-8 w-8 mr-2 rounded-full"
            src={article.author.image}
            alt={article.author.username}
          ></img>
          <h4 className="blue">{article.author.username}</h4>
        </div>
        <p className="text-l py-2">{article.description}</p>
        <BrowserRouter>
          <NavLink
            className="lightBlue mt-4 inline-block border-2 border-solid rounded-lg py-1 px-2 border-blue-400"
            to="/"
          >
            Read More
          </NavLink>
        </BrowserRouter>
      </div>
      <div>
        <a
          href="/"
          className="rounded-lg border-solid border-2 border-green-600 py-1 px-2"
        >
          ðŸ’š{" "}
          <span className="text-sm text-green-600">
            {article.favoritesCount}
          </span>
        </a>
      </div>
    </div>
  );
}

export default Articles;
