import React from "react";
import { NavLink } from "react-router-dom";
import ArticlesFeed from "./articlesFeed";
import Feed from "./feed";
import Home from "./home";

class Profile extends React.Component {
  constructor() {
    super();
    this.state = {
      articles: null,
      articlesCount: null,
      author: "",
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = () => {
    let authorFilter;
    authorFilter = `&author=${this.props.user.username}`;

    fetch(
      `https://mighty-oasis-08080.herokuapp.com/api/articles?limit=10&offset=${
        this.state.activeIndex * 10
      }${authorFilter}`
    )
      .then((res) => {
        if (!res.ok) {
          throw new Error(res.statusText);
        }
        return res.json();
      })
      .then((data) => {
        this.setState({
          articles: data.articles,
          articlesCount: data.articlesCount,
        });
      })
      .catch((err) => {
        this.setState({
          error: "not able to fetch articles",
        });
      });
  };
  handleState = (key, value) => {
    this.setState({
      [key]: value,
    });
    this.fetchData();
  };
  render() {
    return (
      <div>
        <Hero user={this.props.user} />
        <div className="w-3/5 mx-auto">
          <Feed myfeed={true} author={true} favouritedFeed={true} />
          <ArticlesFeed
            articles={this.state.articles}
            openTag={this.state.openTag}
          />
        </div>
      </div>
    );
  }
}

function Hero(props) {
  let { username, email, bio, image } = props.user;
  return (
    <>
      <div className="bg-creme p-4 flex items-center flex-col justify-evenly">
        <div className="flex items-center">
          <img
            className="w-16  rounded-2xl h-16"
            src={image}
            alt={username}
          ></img>
          <h1 className="medBlue text-3xl capitalize p-3">{username}</h1>
        </div>
        <a
          className="  text-xl p-3"
          href="/"
          onClick={(event) => {
            event.preventDefault();
          }}
        >
          {email}
        </a>
        <p className="text-lg">{bio}</p>
        <NavLink to="/settings" className="p-2 underline pink text-base">
          Update settings
        </NavLink>
      </div>
    </>
  );
}

export default Profile;
