import React from "react";

class Profile extends React.Component {
  constructor() {
    super();
    this.state = {};
  }
  render() {
    return (
      <div>
        <Hero user={this.props.user} />
      </div>
    );
  }
}

function Hero(props) {
  let { username, email, bio, image } = props.user;
  return (
    <div className="bg-creme p-4 flex items-center flex-col justify-evenly">
      <div className="flex">
        <img src={image} alt={username}></img>
        <h1 className="medBlue text-3xl capitalize p-3">{username}</h1>
      </div>
      <a
        className=" lightBlue text-lg p-3"
        href="/"
        onClick={(event) => {
          event.preventDefault();
        }}
      >
        {email}
      </a>
      <p className="text-xl">{bio}</p>
    </div>
  );
}

export default Profile;
