import React from "react";
import { NavLink } from "react-router-dom";
import ArticlesFeed from "./articlesFeed";
import Home from "./home";

class Profile extends React.Component {
  constructor() {
    super();
    this.state = {};
  }
  fetchData = () => {
    const tag = this.state.openTag ? `tag=${this.state.openTag}&` : "";
    let authorFilter;
    if (this.state.myfeed === true) {
      authorFilter = `&author=${this.state.author}`;
    }

    fetch(
      `https://mighty-oasis-08080.herokuapp.com/api/articles?${tag}limit=10&offset=${
        this.state.activeIndex * 10
      }${authorFilter ? authorFilter : ""}`
    )
      .then((res) => {
        if (!res.ok) {
          throw new Error(res.statusText);
        }
        return res.json();
      })
      .then((data) => {
        this.setState({
          articles: data.articles,
          articlesCount: data.articlesCount,
        });
      })
      .catch((err) => {
        this.setState({
          error: "not able to fetch articles",
        });
      });
  };
  render() {
    return (
      <div>
        <Hero user={this.props.user} />
        <ArticlesFeed />
      </div>
    );
  }
}

function Hero(props) {
  let { username, email, bio, image } = props.user;
  return (
    <>
      <div className="bg-creme p-4 flex items-center flex-col justify-evenly">
        <div className="flex items-center">
          <img
            className="w-16  rounded-2xl h-16"
            src={image}
            alt={username}
          ></img>
          <h1 className="medBlue text-3xl capitalize p-3">{username}</h1>
        </div>
        <a
          className="  text-xl p-3"
          href="/"
          onClick={(event) => {
            event.preventDefault();
          }}
        >
          {email}
        </a>
        <p className="text-lg">{bio}</p>
        <NavLink to="/settings" className="p-2 underline pink text-base">
          Update settings
        </NavLink>
      </div>
    </>
  );
}

export default Profile;
