class Sidebar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tags: null,
    };
  }
  componentDidMount() {
    this.setState({
      tags: this.props.tags,
    });
  }
  render() {
    console.log(this.state.tags);
    return (
      <div className="w-1/5 bg-pink">
        <h2>sidebar</h2>
      </div>
    );
  }
}
