import React from "react";
import { NavLink } from "react-router-dom";

class Login extends React.Component {
  constructor() {
    super();
    this.state = {};
  }
  render() {
    return (
      <div className="login flex-col p-12 flex justify-center items-center">
        <h2 className="text-2xl p-2 blue">Login</h2>
        <div className="flex flex-row">
          <p className="text-red-500 text-lg">Don't have account?</p>
          <NavLink
            to="/signup"
            className={"text-green-500 text-xl underline px-2"}
          >
            Sign up
          </NavLink>
        </div>
        <form className="flex flex-col">
          <input
            className="text-xl rounded-md w-70 py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
            type="text"
            name="email"
          ></input>
          <input
            type="password"
            name="password"
            className="text-xl rounded-md w-70 py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900"
          ></input>
          <input
            className="text-xl rounded-md w-70 py-1 px-4 my-2 border-2 border-solid border-blue-900 text-blue-900 bg-blue-100"
            type="submit"
            value="Login"
          ></input>
        </form>
      </div>
    );
  }
}
export default Login;
