import React from "react";
import ArticlesFeed from "./articlesFeed";
import Feed from "./feed";
import fetchData from "../utils/fetchData";
import HeroProfile from "./heroProfile";
const url = `https://mighty-oasis-08080.herokuapp.com/api/profiles/${
  window.location.href.split("/")[4]
}`;
let username = window.location.href.split("/")[4];

class Profile extends React.Component {
  constructor() {
    super();
    this.state = {
      articles: null,
      articlesCount: null,
      currentUser: "",
      myfeed: true,
      user: "",
      username: "",
    };
  }
  componentDidMount() {
    this.fetch(url);
  }
  fetch = (url) => {
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        this.setState(
          {
            username: username,
            currentUser: this.props.user,
            user: data.profile,
          },
          () => {
            fetchData(
              this.state.myfeed
                ? `&author=${this.state.user.username}`
                : `&favorited=${this.state.user.username}`,
              0,
              this.handleState
            );
          }
        );
      });
  };
  componentDidUpdate() {
    username = window.location.href.split("/")[4];
    if (this.state.username !== username) {
      this.fetch(url);
    }
  }
  handleState = (key, value) => {
    this.setState(
      {
        [key]: value,
      },
      () => {
        if (key === "myfeed") {
          fetchData(
            this.state.myfeed
              ? `&author=${this.state.user.username}`
              : `&favorited=${this.state.user.username}`,
            0,
            this.handleState
          );
        }
      }
    );
  };
  render() {
    return (
      <div>
        <HeroProfile
          user={this.state.user}
          follow={this.state.user.username !== this.state.currentUser.username}
        />
        <div className="w-3/5 mx-auto">
          <Feed
            myfeed={this.state.myfeed}
            author={true}
            myArticles={true}
            favouritedFeed={true}
            handleState={this.handleState}
          />
          <ArticlesFeed
            articles={this.state.articles}
            openTag={this.state.openTag}
          />
        </div>
      </div>
    );
  }
}

export default Profile;
