import React from "react";
import { BrowserRouter, NavLink } from "react-router-dom";

class Articles extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: null,
    };
  }
  componentDidMount() {
    fetch("https://mighty-oasis-08080.herokuapp.com/api/articles")
      .then((res) => res.json())
      .then((data) => {
        this.setState({
          articles: data,
        });
      })
      .catch((err) => console.log(err));
  }
  render() {
    return (
      <section className="articles">
        {this.state.articles
          ? this.state.articles.articles.map((article, i) => {
              return <Article article={article} key={i} />;
            })
          : ""}
      </section>
    );
  }
}

function Article(props) {
  const article = props.article;
  return (
    <div className="article justify-between flex p-8 m-4 border-2 border-solid rounded-lg border-black">
      <div>
        <h3 className="text-2xl pink">{article.title}</h3>
        <div className="flex py-2 items-center">
          <img
            className="h-8 w-8 mr-2 rounded-full"
            src={article.author.image}
            alt={article.author.username}
          ></img>
          <h4 className="blue">{article.author.username}</h4>
        </div>
        <p className="text-l py-4">{article.description}</p>
        <BrowserRouter>
          <NavLink className="" to="/">
            Read More
          </NavLink>
        </BrowserRouter>
      </div>
      <div>
        <a href="/">heart</a>
      </div>
    </div>
  );
}

export default Articles;
