export default function fetchData(filter, activeIndex, fn, tag) {
  let Tag = tag;
  let authorFilter;
  authorFilter = filter;

  fetch(
    `https://mighty-oasis-08080.herokuapp.com/api/articles?limit=10${Tag}&offset=${
      activeIndex * 10
    }${authorFilter}`
  )
    .then((res) => {
      if (!res.ok) {
        throw new Error(res.statusText);
      }
      return res.json();
    })
    .then((data) => {
      fn("articles", data.articles);
      fn("articlesCount", data.articlesCount);
    })
    .catch((err) => {
      fn("error", "not able to fetch data");
    });
}
