import React from "react";
import { NavLink } from "react-router-dom";
import ArticlesFeed from "./articlesFeed";
import Feed from "./feed";
import fetchData from "../utils/fetchData";

class Profile extends React.Component {
  constructor() {
    super();
    this.state = {
      articles: null,
      articlesCount: null,
      author: "",
      myfeed: true,
      user: null,
    };
  }
  componentDidMount() {
    console.log(
      `https://mighty-oasis-08080.herokuapp.com/api/profiles/${this.props.match.params.username}`
    );
    fetch(
      `https://mighty-oasis-08080.herokuapp.com/api/profiles/${this.props.match.params.username}`,
      {
        method: "GET",
      }
    )
      .then((data) => data.json())
      .then((data) => {
        console.log(data);
        if (data.profile) {
          this.setState({
            user: data.profile,
          });
        }
      });
    this.setState({
      author: this.props.match.params.username,
    });
    fetchData(
      this.state.myfeed
        ? `&author=${this.state.author}`
        : `&favorited=${this.state.author}`,
      0,
      this.handleState
    );
  }

  handleState = (key, value) => {
    this.setState(
      {
        [key]: value,
      },
      () => {
        if (key === "myfeed") {
          fetchData(
            this.state.myfeed
              ? `&author=${this.state.author}`
              : `&favorited=${this.state.author}`,
            0,
            this.handleState
          );
        }
      }
    );
  };
  render() {
    return (
      <div>
        {/* <Hero user={this.props.user} /> */}
        <div className="w-3/5 mx-auto">
          <Feed
            myfeed={this.state.myfeed}
            author={true}
            myArticles={true}
            favouritedFeed={true}
            handleState={this.handleState}
          />
          <ArticlesFeed
            articles={this.state.articles}
            openTag={this.state.openTag}
          />
        </div>
      </div>
    );
  }
}

function Hero(props) {
  let { username, email, bio, image } = props.user;
  return (
    <>
      <div className="bg-creme relative p-4 ">
        <div className="flex items-center flex-col justify-evenly ">
          <div className="flex items-center">
            <img
              className="w-16  rounded-2xl h-16"
              src={image}
              alt={username}
            ></img>
            <h1 className="medBlue text-3xl capitalize p-3">{username}</h1>
          </div>
          <a
            className="  text-xl p-3"
            href="/"
            onClick={(event) => {
              event.preventDefault();
            }}
          >
            {email}
          </a>
          <p className="text-lg">{bio}</p>
        </div>
        <NavLink
          to="/settings"
          className="absolute bottom-2 right-4 p-2 underline pink text-base"
        >
          Update settings
        </NavLink>
      </div>
    </>
  );
}

export default Profile;
