import React from "react";
import { BrowserRouter, NavLink } from "react-router-dom";

class Sidebar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tags: null,
    };
  }
  componentDidMount() {
    fetch("https://mighty-oasis-08080.herokuapp.com/api/articles")
      .then((res) => res.json())
      .then((data) => {
        let Tags = [];
        const tags = data.articles.map((article) => article.tagList);
        tags.reduce((cv, acc) => {
          Tags.push(...cv);
          return acc;
        }, []);
        Tags.sort((tag) => Math.random() - 0.5);
        this.setState({
          tags: Tags,
        });
      })
      .catch((err) => console.log(err));
  }
  render() {
    return (
      <div className="w-1/4 bg-pink">
        <h2 className="align-center text-xl blue p-2">sidebar</h2>
        <div className="tags flex flex-wrap p-2">
          {this.state.tags
            ? this.state.tags.map((tag, i) => {
                return <Tag tag={tag} key={i} />;
              })
            : ""}
        </div>
      </div>
    );
  }
}

function Tag(props) {
  return (
    <BrowserRouter>
      <NavLink
        className="text-m py-1 px-2 m-1 border-solid border rounded-md border-blue-900 blue capitalize"
        to="/"
      >
        {props.tag}
      </NavLink>
    </BrowserRouter>
  );
}

export default Sidebar;
